ctrl:classifier.ctrl:set-value-expression:

if (mcCaseHidden.id == null && mcCase.id == null){
var o = new Object();
o.id = 1;
o.name = 'Новый случай';
o.caseOpenDate = mcCaseDef.caseOpenDate;
o.fundingSourceType = mcCaseDef.fundingSourceType;
o.caseType = mcCaseDef.caseType;
